typedef int testc[(sizeof(info->clocks)>=(sizeof(samp->profile.named)))?1:-1];
for(i=0 ; i<STBIR__ARRAY_SIZE(bdescriptions) ; i++)
info->clocks[i]=samp->profile.array[i+1];
