int32_t turtleTextureUnload(turtle_texture_t texture) {
    /* update list */
    if (texture >= turtle.textureList -> length || texture < 1) {
        return -1;
    }
    free(turtle.textureList -> data[texture].s);
    turtle.textureList -> data[texture].s = strdup("");
    return 0;
}

